

var tabs = document.querySelectorAll('[data-tabs]');
var tabContents = document.querySelectorAll('data-tab-contents');
function activeTab(tab) {
  var tabActiveClasses = ['border-gray-300', 'bg-gray-800', 'text-gray-300'];
  var tabInactiveClasses = ['border-gray-800', 'text-gray-400'];
  tabs.forEach(el => {
      if(el == tab) {
          tabActiveClasses.forEach(addClass => el.classList.add(addClass));
          tabInactiveClasses.forEach(removeClass => el.classList.remove(removeClass));
          // Content
          document.querySelector(el.dataset.tabTarget).classList.remove('hidden');
      } else {
          // tabs inactive
          tabActiveClasses.forEach(addClass => el.classList.remove(addClass));
          tabInactiveClasses.forEach(removeClass => el.classList.add(removeClass));
          // Content hidden
          document.querySelector(el.dataset.tabTarget).classList.add('hidden');
      }
  })
}
activeTab(tabs[0]); /* Default Active */
tabs.forEach(tab => tab.addEventListener('click', ()=> {activeTab(tab)}));

